import time
from multiprocessing import Pool

from scrapy import cmdline


def task(_spider_name, _i):
	time.sleep(1)
	cmdline.execute(f'scrapy crawl {_spider_name} --nolog'.split())

if __name__ == '__main__':
	spider_thread_dict = {'dawanqu':20 ,'huaren':10}
