	pool = Pool(sum([int(_) for _ in scrapy_start_info.values()]))
	for _spider, _process in scrapy_start_info.items():  # 遍历每个spider名称与进程数
		for _p in range(int(_process)):
			# print(_spider, _p)
			pool.apply_async(task, args=(_spider, _p))

